import React from "react";
import { SafeAreaView, View, Image, ScrollView, FlatList } from "react-native";
import {
  Divider,
  Icon,
  Layout,
  Text,
  Button,
  useStyleSheet,
  StyleService,
  useTheme,
} from "@ui-kitten/components";
import Swiper from "react-native-swiper";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";
import CachedImage from "react-native-expo-cached-image";
import { ThemeContext } from "../theme/theme-context";
import { song } from "../test/test-song";
import { Track_1, Playlist_1 } from "./General";

export const TopSwipwer = () => {
  const styles = useStyleSheet(themedStyles);
  return (
    <Layout>
      <Swiper
        containerStyle={styles.wrapper}
        loop={false}
        paginationStyle={{
          bottom: 0,
        }}
        activeDotColor="#75AEFF"
        dotStyle={styles.dots}
        activeDotStyle={styles.dots}
        bounces={true}
      >
        <View style={styles.imgCase}>
          <Image
            source={require("../../assets/ariana.jpg")}
            style={styles.plainImg}
            resizeMethod="scale"
          />
          <View style={styles.imgShadow}></View>
        </View>

        {/* slide 2 */}
        <View style={styles.slide2}>
          <Text style={{ fontFamily: "Sofia" }}>
            Check out the top world's biggest hits.
          </Text>
        </View>

        {/* slide 3 */}
        <View style={styles.slide}>
          <View style={styles.sect}>
            <Text style={styles.title} category="s1" appearance="hint">
              Tracks
            </Text>
            <Text style={styles.subtitle}>50</Text>
          </View>

          <Divider style={styles.vertDivider} />

          <View style={styles.sect}>
            <Text style={styles.title} category="s1" appearance="hint">
              Length
            </Text>
            <Text style={styles.subtitle}>02:35:42</Text>
          </View>

          <Divider style={styles.vertDivider} />

          <View style={styles.sect}>
            <Text style={styles.title} category="s1" appearance="hint">
              Updated
            </Text>
            <Text style={styles.subtitle} numberOfLines={1}>
              one week ago
            </Text>
          </View>
        </View>
      </Swiper>

      <View style={styles.titleCase}>
        <Text category="h5">Hits of the Moment</Text>
        <Text category="s1" appearance="hint">
          By Software - Gospel Pop Editor
        </Text>
      </View>
    </Layout>
  );
};

export const IconContainer = () => {
  const styles = useStyleSheet(themedStyles);
  const themeContext = React.useContext(ThemeContext);
  const theme = useTheme();

  const [heart, setHeart] = React.useState("heart-outline");

  const toggleHeart = () => {
    const nextDot = heart === "heart-outline" ? "heart" : "heart-outline";
    setHeart(nextDot);
  };
  const playIcon = (props) => (
    <Icon
      {...props}
      name="play"
      pack="fontawesome"
      style={{
        height: wp(3.6), //ios 15
        width: wp(3.6),
        marginRight: wp(4.8), //ios 20
        color: "white",
      }}
    />
  );
  return (
    <Layout>
      <View
        style={{
          flexDirection: "row",
          justifyContent: "space-between",
          marginHorizontal: wp(3.6), //ios 15
          marginBottom: 20,
        }}
      >
        <View style={styles.iconOutline}>
          <Icon
            name="arrow-redo-outline"
            pack="ionic"
            style={{
              height: wp(6),
              width: wp(6),
              color: theme["color-primary-500"],
            }} //ios 25
          />
        </View>

        <Button
          style={{
            borderRadius: wp(12.1), //ios 50
            height: wp(10.9), //ios 45
            width: wp(38.61), //ios 160
            shadowColor: "#000",
            shadowOffset: {
              width: 0,
              height: 2,
            },
            shadowOpacity: 0.23,
            shadowRadius: 2.62,

            elevation: 4,
          }}
          status="primary"
          accessoryLeft={playIcon}
          onPress={themeContext.toggleTheme}
        >
          PLAY
        </Button>

        <View style={styles.iconOutline}>
          <Icon
            name={heart}
            pack="ionic"
            style={{
              height: wp(6),
              width: wp(6),
              color: theme["color-primary-500"],
              marginTop: wp(0.6),
            }} //ios 25
            onPress={toggleHeart}
          />
        </View>
      </View>
    </Layout>
  );
};

export const MusicList = () => {
  const styles = useStyleSheet(themedStyles);

  const RenderItem = React.memo(({ item }) => {
    return <Track_1 data={item} />;
  });
  return (
    <Layout level="3" style={styles.musicList}>
      <FlatList
        data={song}
        renderItem={({ item }) => <RenderItem item={item} />}
        showsVerticalScrollIndicator={false}
        removeClippedSubviews={true}
        keyExtractor={(item) => item.id}
      />

      {/* <View style={{ marginVertical: 10 }}>
        {song.map((res, index) => {
          return <Track_1 id={index} key={index} data={res} index={index} />;
        })}
      </View> */}
    </Layout>
  );
};

export const SameArtist = () => {
  const styles = useStyleSheet(themedStyles);
  const sliced = song.slice(0, 8);

  const RenderItem = React.memo(
    ({ item }) => {
      return <Playlist_1 data={item} />;
    },
    (prevProps, nextProps) => {
      return prevProps.item === nextProps.item ? false : true;
    }
  );
  return (
    <Layout level="3">
      <View style={styles.slideTitle}>
        <Text category="h6" style={{ fontFamily: "ProximaBold" }}>
          Same Artist
        </Text>
        <Icon
          style={{ height: 20, width: 20 }}
          fill="#8F9BB3"
          name="arrow-ios-forward-outline"
        />
      </View>

      {/* <FlatList
        data={song}
        renderItem={({ item }) => <RenderItem item={item} />}
        // removeClippedSubviews={true}
        horizontal={true}
        keyExtractor={(item) => item.id}
      /> */}

      {/* <ScrollView horizontal={true} showsHorizontalScrollIndicator={false}>
        {song.map((res, index) => {
          return <Playlist_1 id={index} key={index} data={res} />;
        })}
      </ScrollView> */}
    </Layout>
  );
};

const themedStyles = StyleService.create({
  // playlist top swiper
  imgCase: {
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.22,
    shadowRadius: 2.22,
    elevation: 1,
    height: hp(30),
    alignItems: "center",
  },
  plainImg: {
    height: wp(48.3), // alos 200 ios
    width: wp(48.3), //200 ios
    borderRadius: wp(1.9),
  },
  imgShadow: {
    bottom: wp(48.3),
    backgroundColor: "rgba(0, 0, 0, 0.1)",
    height: wp(48.3),
    width: wp(48.3),
    borderRadius: wp(1.9),
  },
  dots: {
    height: 5,
    width: 5,
    borderRadius: wp(12.1),
  },
  wrapper: {
    height: wp(53.6),
    justifyContent: "center",
    alignItems: "center",
  },
  titleCase: {
    marginTop: wp(3.6),
    marginBottom: wp(8.7),
    justifyContent: "center",
    alignItems: "center",
  },
  slide: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  slide2: {
    alignItems: "center",
    justifyContent: "center",
    height: wp(48.3),
  },
  vertDivider: {
    height: wp(12.1),
    width: 2,
  },
  title: {
    fontSize: wp(3.6),
  },
  subtitle: {
    fontSize: wp(4.8), //ios 20
  },
  sect: {
    marginHorizontal: wp(6),
    maxWidth: wp(36.2),
  },

  // ICON CONTAINER
  iconOutline: {
    height: wp(10.9), //ios 45
    width: wp(10.9),
    borderRadius: wp(12.1), //ios 50
    borderWidth: 0.3,
    justifyContent: "center",
    alignItems: "center",
    borderColor: "#8F9BB3",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 3,
    },
    shadowOpacity: 0.27,
    shadowRadius: 4.65,
  },

  //MUSIC LIST
  musicList: {
    minHeight: 310,
    borderTopColor: "#8F9BB3",
    borderTopWidth: 0.3,
  },
  // SAME ARTIST
  slideTitle: {
    marginVertical: wp(2.8),
    justifyContent: "space-between",
    alignItems: "center",
    flexDirection: "row",
    marginHorizontal: wp(2.5),
  },
});
