import React from "react";
import { SafeAreaView, View, Image, ScrollView } from "react-native";
import {
  Layout,
  Text,
  Icon,
  Button,
  TopNavigation,
  TopNavigationAction,
  useStyleSheet,
  StyleService,
} from "@ui-kitten/components";

import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";
import CachedImage from "react-native-expo-cached-image";
import { ThemeContext } from "../theme/theme-context";

const DownIcon = (props) => (
  <Icon {...props} name="arrow-ios-downward-outline" />
);

export const TopNav = ({ navigation }) => {
  const Butt = () => {
    navigation.navigate("Butt");
  };

  const DownAction = () => (
    <TopNavigationAction icon={DownIcon} onPress={Butt} />
  );
  const styles = useStyleSheet(themedStyles);
  return (
    <Layout>
      <TopNavigation
        alignment="center"
        title="Hits of the Moment"
        subtitle="Playlist"
        accessoryLeft={DownAction}
      />
    </Layout>
  );
};

export const MusicArt = () => {
  const styles = useStyleSheet(themedStyles);
  return (
    <Layout style={{ justifyContent: "center", alignItems: "center" }}>
      <View style={styles.artCase}>
        <CachedImage
          source={{
            uri: "https://www.hdwallpapers.in/download/dua_lipa_4k_2-HD.jpg",
          }}
          style={styles.artImg}
        />
        <View
          style={{
            bottom: wp(42.3),
            backgroundColor: "rgba(0, 0, 0, 0.02)",
            height: wp(42.3),
            width: wp(42.3),
            borderRadius: wp(1.9),
          }}
        ></View>
      </View>
    </Layout>
  );
};

export const IconCase = () => {
  const styles = useStyleSheet(themedStyles);
  const themeContext = React.useContext(ThemeContext);
  return (
    <Layout style={{ alignItems: "center", marginVertical: wp(4) }}>
      <View
        style={{
          flexDirection: "row",
          justifyContent: "space-between",
          alignItems: "center",
          width: wp(50),
        }}
      >
        <Icon
          name="undo-outline"
          style={{ height: wp(7), width: wp(7) }} //ios 25
          fill="#8F9BB3"
        />

        <View style={styles.iconOutline}>
          <Icon
            name="redo-outline"
            pack="ionic"
            style={{
              color: "#8F9BB3",
              fontSize: wp(7),
            }}
          />
        </View>

        <Icon
          name="random"
          pack="fontawesome"
          style={{
            height: wp(7),
            width: wp(7),
            fontWeight: "100",
            fontSize: wp(4),
            color: "#8F9BB3",
          }} //ios 25
          fill="#8F9BB3"
        />
      </View>
    </Layout>
  );
};

const themedStyles = StyleService.create({
  // MUSIC ART
  artCase: {
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 1,
    },
    shadowOpacity: 0.22,
    shadowRadius: 2.22,
    elevation: 3,
    height: wp(90),
    width: wp(90), //175 ios
    borderRadius: wp(1.9),
  },
  artImg: {
    height: wp(90), // alos 175 ios
    width: wp(90), //175 ios
    borderRadius: wp(1.9),
  },

  //   ICON CASE
  iconOutline: {
    height: wp(12.9), //ios 45
    width: wp(12.9),
    borderRadius: wp(12.1), //ios 50
    borderWidth: 0.3,
    justifyContent: "center",
    alignItems: "center",
    borderColor: "#8F9BB3",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 3,
    },
    shadowOpacity: 0.27,
    shadowRadius: 4.65,
  },
});
