import React from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import {
  Layout,
  BottomNavigation,
  BottomNavigationTab,
  Icon,
  useStyleSheet,
  StyleService,
} from "@ui-kitten/components";
import { HomeScreen } from "../screens/Home";
import { DetailsScreen } from "../screens/Details";
import Track from "../../track";

const { Navigator, Screen } = createBottomTabNavigator();
const PersonIcon = (props) => <Icon {...props} name="person-outline" />;
const Music = (props) => <Icon {...props} name="music-outline" />;
const Heart = (props) => <Icon {...props} name="heart-outline" />;
const Search = (props) => <Icon {...props} name="search-outline" />;
const MicIcon = (props) => <Icon {...props} name="mic-outline" />;

const BottomNav = ({ navigation, state }) => {
  const styles = useStyleSheet(themedStyles);
  return (
    <Layout>
      <BottomNavigation
        selectedIndex={state.index}
        onSelect={(index) => navigation.navigate(state.routeNames[index])}
        appearance="noIndicator"
        style={styles.butt}
      >
        <BottomNavigationTab icon={Music} title="Music" />
        <BottomNavigationTab icon={MicIcon} title="Shows" />
        <BottomNavigationTab icon={Heart} title="Favorites" />
        <BottomNavigationTab icon={Search} title="Search" />
        <BottomNavigationTab icon={PersonIcon} title="Profile" />
      </BottomNavigation>
    </Layout>
  );
};

export const BottNavigator = () => (
  <Navigator headerMode="none" tabBar={(props) => <BottomNav {...props} />}>
    <Screen name="Home" component={HomeScreen} />
    <Screen name="Details" component={DetailsScreen} />
    <Screen name="Track" component={Track} />
  </Navigator>
);

const themedStyles = StyleService.create({
  butt: {
    backgroundColor: "background-basic-color-3",
    borderTopLeftRadius: 15,
    borderTopRightRadius: 15,
  },
});
