import React from "react";
import {
  Layout,
  Text,
  Icon,
  Button,
  useStyleSheet,
  StyleService,
} from "@ui-kitten/components";

import { Image, View, StyleSheet } from "react-native";
import CachedImage from "react-native-expo-cached-image";
import { BlurView } from "expo-blur";
import { ThemeContext } from "../theme/theme-context";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";

import { playlist } from "../test/test-playlist";
import { artist } from "../test/test-artist";

export const ToggleTheme = () => {
  const themeContext = React.useContext(ThemeContext);
  return (
    <Button
      status="info"
      style={{ marginVertical: 14 }}
      onPress={themeContext.toggleTheme}
    >
      TOGGLE THEME
    </Button>
  );
};

export const Playlist_1 = ({ data }) => {
  const styles = useStyleSheet(themedStyles);
  return (
    <View style={{ width: wp(42.3), height: wp(58), marginLeft: wp(3.6) }}>
      <View style={styles.imgCase}>
        <CachedImage source={{ uri: data.Image }} style={styles.plainImg} />
        <View
          style={{
            bottom: wp(42.3),
            backgroundColor: "rgba(0, 0, 0, 0.02)",
            height: wp(42.3),
            width: wp(42.3),
            borderRadius: wp(1.9),
          }}
        ></View>
      </View>
      <View style={styles.playcircle}>
        <Icon
          style={{
            height: wp(2.4), //10 ios
            width: wp(2.4),
            marginHorizontal: wp(2.4),
            marginVertical: wp(1.9),
            color: "black",
          }}
          name="play"
          pack="fontawesome"
        />
      </View>

      <Text style={styles.plainSub} numberOfLines={2}>
        {data.name} {wp(42.3)}
      </Text>
      <Text appearance="hint" category="c2" numberOfLines={1}>
        {data.tracks.length} tracks
      </Text>
    </View>
  );
};

export const Playlist_2 = ({ data }) => {
  const styles = useStyleSheet(themedStyles);
  return (
    <View style={{ width: wp(42.3), height: wp(42.3), marginLeft: wp(3.6) }}>
      <View style={styles.imgCase2}>
        <CachedImage source={{ uri: data.Image }} style={styles.plainImg2} />
        <View
          style={{
            bottom: wp(42.3),
            backgroundColor: "rgba(0, 0, 0, 0.02)",
            height: wp(42.3),
            width: wp(42.3), //175 ios
            borderRadius: wp(3.6), //ios 15
          }}
        ></View>
      </View>

      {/* below the image */}
      <BlurView
        intensity={70}
        style={[StyleSheet.absoluteFill, styles.blurslate]}
      >
        <View style={{ marginLeft: wp(2.4) }}>
          <Text style={styles.plainSub2} category="p1" numberOfLines={1}>
            {data.name}
          </Text>
          <Text category="c1" numberOfLines={1}>
            {data.tracks.length}
          </Text>
        </View>
        <View style={styles.playcircle2}>
          <Icon
            style={{
              height: wp(2.4), //ios 10
              width: wp(2.4),
              marginHorizontal: wp(2.4),
              marginVertical: 8,
              color: "black",
            }}
            name="play"
            pack="fontawesome"
          />
        </View>
      </BlurView>
    </View>
  );
};

export const Playlist_3 = ({ data }) => {
  const styles = useStyleSheet(themedStyles);
  return (
    <View style={styles.case3}>
      <View style={styles.imgCase3}>
        <CachedImage source={{ uri: data.Image }} style={styles.plainImg3} />
        <View
          style={{
            bottom: wp(42.3), //175 ios
            backgroundColor: "rgba(0, 0, 0, 0.02)",
            height: wp(42.3),
            width: wp(42.3),
            borderRadius: 5,
          }}
        ></View>
      </View>
      <View style={styles.playcircle3}>
        <Icon
          style={{
            height: wp(2.4), //ios 10
            width: wp(2.4), //ios 10
            marginHorizontal: wp(2.4), //ios 10
            marginVertical: 8,
            color: "black",
          }}
          name="play"
          pack="fontawesome"
        />
      </View>
