import React, { Profiler } from "react";
import {
  Layout,
  Text,
  Icon,
  Button,
  useStyleSheet,
  StyleService,
  Card,
  Divider,
  Avatar,
} from "@ui-kitten/components";
import {
  Image,
  StyleSheet,
  View,
  RefreshControl,
  ScrollView,
  SafeAreaView,
} from "react-native";
import Constants from "expo-constants";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from "react-native-responsive-screen";

const Arrow = (props) => <Icon name="arrow-ios-forward-outline" {...props} />;

export const CardScreen = ({ navigation }) => {
  const styles = useStyleSheet(themedStyles);
  // REFRESH THE SCREEN
  const [gpuRender, setGpuRender] = React.useState(false);

  !gpuRender ? setGpuRender(true) : null;

  return (
    <SafeAreaView style={{ marginTop: Constants.statusBarHeight }}>
      <Layout>
        {/* PLAYLIST 4 */}
        <View>
          <View
            style={{
              height: wp(10),
              display: "flex",
              flexDirection: "row",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <Text style={styles.topText}>
              Your debit card usage summary appear here
            </Text>
          </View>
          <Divider />

          <View>
            <View
              style={{
                display: "flex",
                flexDirection: "row-reverse",
                alignItems: "center",
                marginTop: wp(5),
              }}
            >
              <Icon
                style={styles.icon}
                fill="#8F9BB3"
                name="chevron-up-outline"
              />
              <Text>Filter</Text>
            </View>

            <View style={{ display: "flex", flexDirection: "row" }}>
              <View style={{ display: "flex", flexDirection: "row" }}>
                <Text>From</Text>

                <Button
                  style={{ height: wp(5), width: wp(5) }}
                  status="primary"
                  accessoryRight={Arrow}
                >
                  <Text>28 NOV 2020</Text>
                </Button>
              </View>

              <View style={{ display: "flex", flexDirection: "row" }}>
                <Text>To</Text>
              </View>
            </View>

            <View>
              <Text>All time</Text>
            </View>
            <View>
              <Text>Apply button too</Text>
            </View>
          </View>
        </View>
      </Layout>
    </SafeAreaView>
  );
};

const themedStyles = StyleService.create({
  topText: {
    fontFamily: "Roboto",
    fontStyle: "normal",
    fontWeight: "normal",
    color: "rgba(0, 0, 0, 0.37)",
  },
  filterText: {
    // position: "absolute",
    // height: 19,
    // top: wp(25),
    fontFamily: "Roboto",
    fontStyle: "normal",
    fontWeight: "normal",
    // fontSize: 20,
    // lineHeight: 16,
    color: "#252733",
  },
  icon: {
    width: 32,
    height: 32,
  },
});
