import React from "react";
import { Dimensions, Image, StyleSheet } from "react-native";

const { width } = Dimensions.get("window");
const ratio = 228 / 362;
export const CARD_WIDTH = width * 0.8;
export const CARD_HEIGHT = CARD_WIDTH * ratio;
export const assets = [
  require("../../assets/cards/card1.png"),
  require("../../assets/cards/card2.png"),
  require("../../assets/cards/card3.png"),
  require("../../assets/cards/card4.png"),
  require("../../assets/cards/card5.png"),
  require("../../assets/cards/card6.png"),
];

const styles = StyleSheet.create({
  card: {
    width: CARD_WIDTH,
    height: CARD_HEIGHT,
  },
});

export enum Cards {
  Card1 = 0,
  Card2 = 1,
  Card3 = 2,
  Card4 = 3,
  Card5 = 4,
  Card6 = 5,
}

export const cards = [
  Cards.Card1,
  Cards.Card2,
  Cards.Card3,
  Cards.Card4,
  Cards.Card5,
  Cards.Card6,
];

interface CardProps {
  card: Cards;
}

const Card = ({ card }: CardProps) => {
  return <Image style={styles.card} source={assets[card]} />;
};

export default Card;
